import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Userpost from './components/Userpost';
import './components/style/Youraccount.css';
import { buildTimeValue } from '@testing-library/user-event/dist/utils';

const Youraccount = (props) => {
  const initialposts = [];
  const initialsearched = [];
  const [posts, setposts] = useState(initialposts);
  const [search, setsearch] = useState(initialsearched);
  const [form, setForm] = useState(false);
  const[credentials,setcredentials] = useState({username:"",full_name:"",bio:"",img:""})
  const token = sessionStorage.getItem('token');
  const searched_name = sessionStorage.getItem('username');
  
  const fetchallposts = async () => {
    let response = await fetch(`http://127.0.0.1:8000/api/core/any-user-post/${searched_name}`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    let json = await response.json();
    setposts(json.payload);
  }

  const fetchalldata = async () => {
    let response = await fetch(`http://127.0.0.1:8000/api/accounts/profile/`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    let json = await response.json();
    console.log("Profile:",json);
    setsearch(json);
    setcredentials({use})
  }

 
  const editprofile = async (e) => {
    e.preventDefault();
     await fetch(`http://127.0.0.1:8000/api/accounts/profile/`, {
      method: 'PATCH',
      headers: { 'Authorization': `Bearer ${token}`, body: JSON.stringify({
        username: credentials.username,
        full_name: credentials.full_name,
        bio: credentials.bio,
        image: credentials.img
      }) }
    });

  }

  let navigate = useNavigate();
  const { showalert } = props;

  useEffect(() => {
    if (!token) {
      return navigate('/login');
    }
    fetchalldata();
    fetchallposts();
  }, []); // Added token as a dependency

  console.log(credentials);
  const clicked = () => {
    setForm(true);
  }

  const changed =(e)=>{
    setcredentials({...credentials, [e.target.name]:e.target.value})
  }


  const second_clicked = () => {
    setForm(false);
  }

  return (
    <div className='profile-main-container'>
      <div className="profile-first-container" key={search.id}>
        <div className='image-name'>
          <div className="account-image">
            <img src={search.image} alt="Profile" />
          </div>
          <div className="profile-details">
            <ul>
              <li>
                <h1>{search.full_name}</h1>
              </li>
              <li>
                <h3>@{search.username}</h3>
              </li>
              <li className='Edit-button'>
                <button onClick={clicked}>Edit</button>
              </li>
            </ul>
          </div>
        </div>

        <div className="profile-biodetails">
          <h4>About</h4>
          <div className="profile-bio">
            {search.bio}
          </div>
        </div>
      </div>

      <div className="profile-secondmain-container">
        {form ? (
          <form method="post" className='edit-form' encType="multipart/form-data" onSubmit={editprofile}>
            <label htmlFor='Edit'>Edit your profile</label>
            <br /><br />
            <label htmlFor="fullname">Full Name: </label>
            <input type="text" id="edit-fullname" name="fullname" onChange={changed} value={credentials.full_name} required /><br /><br />

            <label htmlFor="username">Username: </label>
            <input type="username" id="edit-username" value={credentials.username} onChange={changed} name="username" /><br /><br />

            <label htmlFor="about">About: </label>
            <input type="text" id="edit-about" value={credentials.bio} onChange={changed} name="about" required /><br /><br />

            <label htmlFor="imgUpload">Upload Image:</label>
            <input type="file" id="imgUpload" name="imgUpload" onChange={changed} accept="image/*" required /><br /><br />

            <input type="submit" value="Submit Form" />
            <button onClick={second_clicked}>Cancel</button>
          </form>
        ) : (
          <div className="all-posts">
            {Array.isArray(posts) && posts.length > 0 ? (
              posts.map((element) => (
                <Userpost key={element.id} post={element} />
              ))
            ) : (
              <p>No posts available</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Youraccount;
