import React from 'react';
import './style/Postitem.css';
import { useNavigate } from 'react-router-dom';

const Postitem = ({ post, modalResult, realresult, showalert }) => {
  const token = sessionStorage.getItem('token');
  const navigate = useNavigate();

  const clicked = async () => {
    const emails = modalResult ? realresult.email : post.email;

    let response = await fetch('http://127.0.0.1:8000/api/core/connect/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify({
        email: emails,
      }),
    });

    let json = await response.json();
    console.log(json);

    showalert('Email has been sent', 'success');
  };

  const data = modalResult ? realresult : post;

  return (
    <div className='postitem-main-container'>
      <div className="postitem-first-container">
        <div className="postitem-subfirst-container">
          <ul>
            <li id='name'> 
              <div className="circle"></div>
              {data.username}
            </li>
            <li className='connect-button'>
              <button onClick={clicked}>Connect</button>
            </li>
          </ul>
        </div>

        <div className="postitem-subsecond-container">
          <ul>
            <li id='Title'>{data.title}</li>
            <li id='Description'>{data.description}</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Postitem;
