                        <div className="refer">
                        <Link id='refer' to="/signup">
                            <div className="link">
                                Don't have an account?
                                <Link id='link' to="/signup">Signup</Link>
                            </div>
                        </Link>
                    </div>

                    </div>

                </form>
            </div>
        </div>
    )
}

