import React, { useEffect, useState } from 'react';
import './style/Profile.css';
import { useNavigate } from 'react-router-dom';
import Userpost from './Userpost';

const Profile = (props) => {
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState([]);
  const navigate = useNavigate();
  const { showalert } = props;

  // Extract session storage values outside of the component
  const token = sessionStorage.getItem('token');
  const searchedName = sessionStorage.getItem('username1');

  const fetchAllPosts = async () => {
    if (!token || !searchedName) return;

    try {
      let response = await fetch(`http://127.0.0.1:8000/api/core/any-user-post/${searchedName}`, {
        method: 'GET',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (!response.ok) throw new Error('Failed to fetch posts');

      let json = await response.json();
      setPosts(json.payload || []);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  const fetchAllData = async () => {
    if (!token || !searchedName) return;

    try {
      let response = await fetch(`http://127.0.0.1:8000/api/accounts/search/${searchedName}`, {
        method: 'GET',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (!response.ok) throw new Error('Failed to fetch user data');

      let json = await response.json();
      // console.log(json);
      setSearch(json || []);
      sessionStorage.setItem('user_id', json[0]?.user_id || '');
      // sessionStorage.setItem('online_status', json[0]?.online_status || '');
    } catch (error) {
      console.error('Error fetching search data:', error);
    }
  };

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }
    fetchAllData();
    fetchAllPosts();
  }, [token, navigate, searchedName]);

  const handleClick = () => {
    navigate("/message");
  };

  return (
    <div className='profile-main-container'>
      {Array.isArray(search) && search.length > 0 ? (
        search.map((element) => (
          <div className="profile-first-container" key={element.id}>
            <div className='image-name'>
              <div className="account-image">
                <img src={element.image} alt="Profile" />
              </div>
              <div className="profile-details">
                <ul>
                  <li>
                    <h1>{element.full_name}</h1>
                  </li>
                  <li>
                    <h3>@{element.username}</h3>
                  </li>
                  <li className='connect-button'>
                    <button onClick={handleClick}>Connect</button>
                  </li>
                </ul>
              </div>
            </div>
            <div className="profile-biodetails">
              <h2 style={{ fontSize: '20px' }}>About:</h2>
              <div className="profile-bio">
                {element.bio}
              </div>
            </div>
          </div>
        ))
      ) : (
        <p>No user data available</p>
      )}
      <div className="profile-secondmain-container">
        <div className="all-posts">
          {Array.isArray(posts) && posts.length > 0 ? (
            posts.map((element) => (
              <Userpost key={element.id} post={element} />
            ))
          ) : (
            <p>No posts available</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
