import React, { useEffect, useState } from 'react';
import './style/Postitem.css';
import { useNavigate } from 'react-router-dom';

const Postitem = ({ post, showalert }) => {
  const token = sessionStorage.getItem('token');
  const navigate = useNavigate();

  const [modalResult, setModalResult] = useState(true);
  // const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [realresult, setRealresult] = useState(null); // State for holding the realresult
  const category = sessionStorage.getItem('category');

  const fetchData = async () => {
    try {
      let response = await fetch(`http://127.0.0.1:8000/api/core/search/${category}`, {
        method: 'GET',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const data = await response.json();
      const actualData = data.payload;
      console.log(actualData.length);
      if (actualData.length > 0) {
        setSearchResults(actualData);
        setRealresult(actualData[0]); // Assuming you want the first result
        setModalResult(true);
      } else {
        setSearchResults([]);
        setRealresult(null); // No results
        setModalResult(false);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    if (category) {
      fetchData();
    }
  }, [category]);

  const clicked = async () => {
    const emails = post.email;

    let response = await fetch('http://127.0.0.1:8000/api/core/connect/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify({
        email: emails,
      }),
    });

    let json = await response.json();
    console.log(json);

    showalert('Email has been sent', 'success');
    // navigate("/message")
  };

  return (
    <div className='postitem-main-container'>
      <div className="postitem-first-container">
        <div className="postitem-subfirst-container">
          <ul style={modalResult ? { display: 'none' } : { display: 'flex' }}>
            <li id='name'> <div className="circle"></div>{post?.username}</li>
            <li className='connect-button'><button onClick={clicked}>Connect</button></li>
          </ul>
          <ul style={!modalResult ? { display: 'none' } : { display: 'flex' }}>
            <li id='name'> <div className="circle"></div>{realresult?.username}</li>
            <li className='connect-button'><button onClick={clicked}>Connect</button></li>
          </ul>
        </div>

        <div className="postitem-subsecond-container">
          <ul style={modalResult ? { display: 'none' } : { display: 'flex' }}>
            <li id='Title'>{post?.title}</li>
            <li id='Description'>{post?.description}</li>
          </ul>
        </div>

        <ul style={!modalResult ? { display: 'none' } : { display: 'flex' }}>
          <li id='Title'>{realresult?.title}</li>
          <li id='Description'>{realresult?.description}</li>
        </ul>
      </div>
    </div>
  );
};

export default Postitem;
