import { useEffect, useState } from "react";
import NoteContext from "./navbarContext";

const NoteState =(props) =>{
    const [modalResult, setModalResult] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    // const [loading, setLoading] = useState(false);
    const token = sessionStorage.getItem('token');
    const category = sessionStorage.getItem('category');

    const fetchdata = async() =>{
        let response = await fetch(`http://127.0.0.1:8000/api/core/search/${category}`, {
            method: 'GET',
            headers: { 'Authorization': `Bearer ${token}` }
        });
        const data = await response.json();
        const actualData = data.payload;
        console.log(actualData.length)
        if (actualData.length > 0) {
            setSearchResults(actualData);
            // console.log(data)
            setModalResult(true);
        } else {
            setSearchResults([]);
            setModalResult(false);
        }
    }

    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
      console.log(e.target.value)}
    return(
        <NoteContext.Provider value={{fetchdata,handleSearch}}>
        {props.children}
    </NoteContext.Provider>
    )

}

export default NoteState;
