import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Userpost from './components/Userpost';
import './components/style/Youraccount.css';

const Youraccount =(props)=>{
    const initialposts = [];
    const initialsearched = [];
    const [posts, setposts] = useState(initialposts);
    const [search, setsearch] = useState(initialsearched);
    const token = sessionStorage.getItem('token');
    const searched_name = sessionStorage.getItem('username')
    const fetchallposts = async () => {
      let response = await fetch(`http://127.0.0.1:8000/api/core/any-user-post/${searched_name}`, {
        method: 'GET',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      let json = await response.json();
      // console.log(json)
      setposts(json.payload);
      
    }
  

  
    const fetchalldata = async () => {
      let response = await fetch(`http://127.0.0.1:8000/api/accounts/profile/`, {
        method: 'GET',
        headers: { 'Authorization':`Bearer ${token}` }
      });
      let json = await response.json();
      console.log("json:",json)
      setsearch(json);
      
    }
  
    let navigate = useNavigate();
    const { showalert } = props;
  
    useEffect(() => {
  
      if (sessionStorage.getItem('token') !== token || !token) {
        return navigate('/login');
      }
      fetchalldata();
      fetchallposts();
      // console.log(search);
  
    }, [])
  
    const clicked = async() =>{
  
    //   const emails = sessionStorage.getItem('email');
      console.log(emails);
  
      let response = await fetch('http://127.0.0.1:8000/api/core/connect/', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json','Authorization': `Bearer ${token}` },
          body: JSON.stringify({
              email: emails
          })
        });
        let json = await response.json();
        // console.log(json);
      props.showalert('Email has been sent', 'success');
      
      // navigate("/message")
  }
    return (
      
      <div className='profile-main-container'>

        <div className="profile-first-container" key={search.id}>
          <div className='image-name'>
          <div className="account-image">
            <img src={search.image} />
          </div>
          <div className="profile-details">
            <ul>
              <li>
                <h1>{search.full_name}</h1>
              </li>
              <li>
                <h3>@{search.username}</h3>
              </li>
              <li className='Edit-button'>
                {/* <button onClick={clicked}>Connect</button> */}
              </li>
            </ul>
          </div>
          </div>
  
          <div className="profile-biodetails">
            <h4>About</h4>
            <div className="profile-bio">
              {search.bio}
            </div>
          </div>
        </div>
        {/* <h2 id='POSTS'>Posts:</h2> */}
        <div className="profile-secondmain-container">
          <div className="all-posts">
            {Array.isArray(posts) && posts.length > 0 ? (
              posts.map((element) => (
                <Userpost key={element.id} post={element} />
              ))
            ) : (
              <p>No posts available</p>
            )}
            {/* <Userpost/> */}
            
          </div>
        </div>
      </div>
    );
}

export default Youraccount;
