import React, { useState } from 'react';
import './style/Navbar.css';
import { useLocation, useNavigate } from 'react-router-dom';
import realimage from './style/PartnurUp.png';
import searchbtn from './style/search-interface-symbol.png';
import Post from './Post';
import Postitem from './Postitem';

const Navbar = () => {
  let navigate = useNavigate();
  const location = useLocation();

  const [modalResult, setModalResult] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const token = sessionStorage.getItem('token');

  const fetchData = async () => {
    setLoading(true);
    try {
      const response = await fetch(`http://127.0.0.1:8000/api/core/search/${searchTerm}`, {
        method: 'GET',
        headers: { 'Authorization': `Bearer ${token}` },
      });
      const data = await response.json();
      const actualData = data.payload;

      if (Array.isArray(actualData) && actualData.length > 0) {
        setSearchResults(actualData);
        setModalResult(true);
      } else {
        setSearchResults([]);
        setModalResult(false);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const logout = () => {
    sessionStorage.clear();
    navigate('/');
  };

  return (
    <div className='nav-main-container' style={location.pathname === '/' ? { display: 'none' } : {}}>
      <div className="nav-first-container">
        <ul>
          <img src={realimage} id='nav-logo' alt="logo" width="50vw" />
          <li id='Logo'>Project Pals</li>
          <li>
            <div className="nav-search">
              <input type="text" placeholder='Search...' value={searchTerm} onChange={handleSearch} />
              <img src={searchbtn} alt="Search" onClick={fetchData} />
            </div>
          </li>
          <li className='nav-list'><a href='/aboutus'>About us</a></li>
          <li className='nav-list'><a href='/post'>Posts</a></li>
          <li className='nav-list'><a href='/message'>Messages</a></li>
          <li id='options'>Options</li>
          {token ? (
            <li className='nav-signup'><button onClick={logout}>Logout</button></li>
          ) : (
            <>
              <li className="nav-signup"><a href='/signup'><button>Sign Up</button></a></li>
              <li className="nav-signup"><a href='/login'><button>Login</button></a></li>
            </>
          )}
        </ul>
      </div>

      {searchResults.length > 0 && searchResults.map((element) => (
        <Post key={element.id} modalResult={modalResult} realresult={element} />
      ))}
    </div>
  );
};

export default Navbar;
