import React, { useEffect, useState } from 'react';
import './style/Profile.css';
import { useNavigate } from 'react-router-dom';
import Userpost from './Userpost';

const Profile = (props) => {
  const initialposts = [];
  const initialsearched = [];
  const [posts, setPosts] = useState(initialposts);
  const [search, setSearch] = useState(initialsearched);
  const token = sessionStorage.getItem('token');

  const fetchAllPosts = async () => {
    let response = await fetch('http://127.0.0.1:8000/api/core/user-posts/', {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    let json = await response.json();
    setPosts(json);
  };

  const fetchSearchResults = async () => {
    const searched_name = sessionStorage.getItem('username');
    let response = await fetch(`http://127.0.0.1:8000/api/accounts/search/${searched_name}`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    let json = await response.json();
    setSearch(json);
  };

  let navigate = useNavigate();
  const { showalert } = props;

  useEffect(() => {
    if (sessionStorage.getItem('msg') !== "login success") {
      return navigate('/login');
    }
    fetchAllPosts(); // Fetch posts on component mount
    fetchSearchResults(); // Fetch search results for the initial username
  }, []); // No dependency on searchTerm

  const handleConnectClick = async () => {
    const emails = search.email;
    let response = await fetch('http://127.0.0.1:8000/api/core/connect/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
      body: JSON.stringify({
        email: emails
      })
    });
    let json = await response.json();
    props.showalert('Email has been sent', 'success');
  };

  return (
    <div className='profile-main-container'>
      {Array.isArray(search) && search.length > 0 ? (
        search.map((element) => (
          <div className="profile-first-container" key={element.id}>
            <div className="profile-details">
              <ul>
                <li><h1>{element.full_name}</h1></li>
                <li><h3>{element.username}</h3></li>
                <li className='connect-button'>
                  <button onClick={handleConnectClick}>Connect</button>
                </li>
              </ul>
            </div>
            <div className="profile-biodetails">
              <div className="profile-collegename">
                <ul>
                  <li><div className="circle"></div></li>
                  <li>Dronacharya College of Engineering</li>
                </ul>
              </div>
              <h4>About</h4>
              <div className="profile-bio">
                Lorem ipsum dolor sit amet consectetur adipisicing elit.
              </div>
            </div>
          </div>
        ))
      ) : (
        <p>No results found</p>
      )}
      <h2 id='POSTS'>Posts:</h2>
      <div className="post-secondmain-container">
        <div className="all-posts">
          {Array.isArray(posts) && posts.length > 0 ? (
            posts.map((element) => (
              <Userpost key={element.id} post={element} />
            ))
          ) : (
            <p>No posts available</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
