import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './style/Post.css';
import Postitem from './Postitem';

const Post = (props) => {
  const [credentials, setCredentials] = useState({ title: '', category: '', description: '' });
  const { realresult, modalResult } = props;

  const changed = (e) => {
    setCredentials({
      ...credentials, [e.target.name]: e.target.value,
    });
  };

  const token = sessionStorage.getItem('token');
  const postdata = async (e) => {
    e.preventDefault();

    await fetch('http://127.0.0.1:8000/api/core/create-post/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify({
        title: credentials.title,
        category: credentials.category,
        description: credentials.description,
      }),
    });

    window.location.reload();
  };

  const [posts, setPosts] = useState([]);
  const fetchAllPosts = async () => {
    let response = await fetch('http://127.0.0.1:8000/api/core/show-post/', {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` },
    });
    let json = await response.json();
    setPosts(json.payload);
  };

  let navigate = useNavigate();
  useEffect(() => {
    if (!token) {
      navigate('/login');
    } else {
      fetchAllPosts();
    }
  }, [token]);

  return (
    <div className='post-main-container'>
      <div className="post-secondmain-container">
        <div className="all-posts">
          {modalResult ? (
            realresult && realresult.length > 0 ? (
              realresult.map((element) => (
                <Postitem key={element.id} realresult={element} modalResult={modalResult} showalert={props.showalert} />
              ))
            ) : (
              <p>No search results available</p>
            )
          ) : (
            Array.isArray(posts) && posts.length > 0 ? (
              posts.map((element) => (
                <Postitem key={element.id} post={element} showalert={props.showalert} />
              ))
            ) : (
              <p>No posts available</p>
            )
          )}
        </div>
      </div>
    </div>
  );
};

export default Post;
