    <li id='name'> <div className="circle"></div>{realresult?.username}</li>
    <li className='connect-button'><button onClick={clicked}>Connect</button></li>
  </ul>
</div>}

        {modalResult?(        <div className="postitem-subsecond-container">
          <ul>
            <li id='Title'>{realresult.title}</li>
            <li id='Description'>{post?.description}</li>
          </ul>
        </div>):        <div className="postitem-subsecond-container">
          <ul>
            <li id='Title'>{post?.title}</li>
            <li id='Description'>{post?.description}</li>
          </ul>
        </div>}

      </div>
    </div>
  );
};

export default Postitem;
