import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Userpost from './components/Userpost';
import './components/style/Youraccount.css';

const Youraccount = (props) => {
  const [posts, setposts] = useState([]);
  const [search, setsearch] = useState([]);
  const [form, setForm] = useState(false);
  const [imagePreview, setImagePreview] = useState(null); // State to hold image preview
  const [credentials, setcredentials] = useState({
    username: "",
    full_name: "",
    bio: "",
    img: null // Handle the image here
  });

  const token = sessionStorage.getItem('token');
  const searched_name = sessionStorage.getItem('username');

  // Fetch posts function
  const fetchallposts = async () => {
    let response = await fetch(`http://127.0.0.1:8000/api/core/any-user-post/${searched_name}`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    let json = await response.json();
    setposts(json.payload);
  };

  // Fetch user profile data
  const fetchalldata = async () => {
    let response = await fetch(`http://127.0.0.1:8000/api/accounts/profile/`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    let json = await response.json();
    console.log("Profile:", json);
    setsearch(json);
    setcredentials({
      username: json.username,
      full_name: json.full_name,
      bio: json.bio,
      img: json.image // Set the image URL from fetched data
    });
    setImagePreview(json.image); // Set initial image preview
  };

  // Handle profile edit
  const editprofile = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('username', credentials.username);
    formData.append('full_name', credentials.full_name);
    formData.append('bio', credentials.bio);
    if (credentials.img instanceof File) {
      formData.append('image', credentials.img); // Append new file if a new image is uploaded
    }

    await fetch(`http://127.0.0.1:8000/api/accounts/profile/`, {
      method: 'PATCH',
      headers: {
        'Authorization': `Bearer ${token}`
      },
      body: formData // Send the formData including file data
    });
  };

  let navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      return navigate('/login');
    }
    fetchalldata();
    fetchallposts();
  }, []); // Fetch data on mount

  // Handle input field changes
  const changed = (e) => {
    const { name, value } = e.target;
    setcredentials((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle file input changes and set preview
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setcredentials((prev) => ({
        ...prev,
        img: file // Set the selected file
      }));

      // Set preview for the newly selected file
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result); // Preview the new image
      };
      reader.readAsDataURL(file);
    }
  };

  const clicked = () => {
    setForm(true);
  };

  const second_clicked = () => {
    setForm(false);
  };

  return (
    <div className='profile-main-container'>
      <div className="profile-first-container" key={search.id}>
        <div className='image-name'>
          <div className="account-image">
            {/* Display image preview */}
            <img src={imagePreview || search.image} alt="Profile" />
          </div>
          <div className="profile-details">
            <ul>
              <li>
                <h1>{search.full_name}</h1>
              </li>
              <li>
                <h3>@{search.username}</h3>
              </li>
              <li className='Edit-button'>
                <button onClick={clicked}>Edit</button>
              </li>
            </ul>
          </div>
        </div>

        <div className="profile-biodetails">
          <h4>About</h4>
          <div className="profile-bio">
            {search.bio}
          </div>
        </div>
      </div>

      <div className="profile-secondmain-container">
        {form ? (
          <form method="post" className='edit-form' encType="multipart/form-data" onSubmit={editprofile}>
            <label htmlFor='Edit'>Edit your profile</label>
            <br /><br />
            <label htmlFor="fullname">Full Name: </label>
            <input
              type="text"
              id="edit-fullname"
              name="full_name"
              onChange={changed}
              value={credentials.full_name}
              required
            /><br /><br />

            <label htmlFor="username">Username: </label>
            <input
              type="text"
              id="edit-username"
              value={credentials.username}
              onChange={changed}
              name="username"
            /><br /><br />

            <label htmlFor="about">About: </label>
            <input
              type="text"
              id="edit-about"
              value={credentials.bio}
              onChange={changed}
              name="bio"
              required
            /><br /><br />

            {/* Image Upload and Preview */}
            <label htmlFor="imgUpload">Upload Image:</label>
            <input
              type="file"
              id="imgUpload"
              name="img"
              onChange={handleFileChange}
              accept="image/*"
            /><br /><br />

            {/* Display preview of the current or new image */}
            {imagePreview && (
              <div className="image-preview">
                <img src={imagePreview} alt="Preview" style={{ width: '150px', height: '150px' }} />
              </div>
            )}

            <input type="submit" value="Submit Form" />
            <button type="button" onClick={second_clicked}>Cancel</button>
          </form>
        ) : (
          <div className="all-posts">
            {Array.isArray(posts) && posts.length > 0 ? (
              posts.map((element) => (
                <Userpost key={element.id} post={element} />
              ))
            ) : (
              <p>No posts available</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Youraccount;
