import React, { useEffect, useState } from 'react';
import './style/Profile.css';
import { useNavigate } from 'react-router-dom';
import Userpost from './Userpost';

const Profile = (props) => {
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState([]);
  const token = sessionStorage.getItem('token');
  const navigate = useNavigate();
  const { showalert } = props;

  // Function to fetch all posts
  const fetchAllPosts = async () => {
    try {
      let response = await fetch('http://127.0.0.1:8000/api/core/user-posts/', {
        method: 'GET',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Failed to fetch posts');
      let json = await response.json();
      setPosts(json);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  // Function to fetch search results
  const fetchSearchResults = async () => {
    const searched_name = sessionStorage.getItem('username1'); // Ensure this key is correct
    try {
      let response = await fetch(`http://127.0.0.1:8000/api/accounts/search/${searched_name}`, {
        method: 'GET',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Failed to fetch search results');
      let json = await response.json();
      setSearch(json);

      // Reload the page after setting search results
      window.location.reload();
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  // Effect to handle session validation and data fetching
  useEffect(() => {
    const sessionStatus = sessionStorage.getItem('msg');
    if (sessionStatus !== "login success") {
      navigate('/login');
    } else {
      fetchAllPosts(); // Fetch posts on component mount
      fetchSearchResults(); // Fetch search results for the initial username
    }
  }, [navigate]); // Dependency on navigate to avoid infinite loops

  // Function to handle the connect button click
  const handleConnectClick = async () => {
    const emails = search.map(item => item.email); // Assuming search is an array
    try {
      let response = await fetch('http://127.0.0.1:8000/api/core/connect/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
        body: JSON.stringify({ email: emails })
      });
      if (!response.ok) throw new Error('Failed to send email');
      let json = await response.json();
      props.showalert('Email has been sent', 'success');
    } catch (error) {
      console.error('Error connecting:', error);
    }
  };

  return (
    <div className='profile-main-container'>
      {Array.isArray(search) && search.length > 0 ? (
        search.map((element) => (
          <div className="profile-first-container" key={element.id}>
            <div className="account-image">
              {/* Add an image or other content here if needed */}
            </div>
            <div className="profile-details">
              <ul>
                <li><h1>{element.full_name}</h1></li>
                <li><h3>{element.username}</h3></li>
                <li className='connect-button'>
                  <button onClick={handleConnectClick}>Connect</button>
                </li>
              </ul>
            </div>
            <div className="profile-biodetails">
              <div className="profile-collegename">
                <ul>
                  <li><div className="circle"></div></li>
                  <li>Dronacharya College of Engineering</li>
                </ul>
              </div>
              <h4>About</h4>
              <div className="profile-bio">
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Vero quaerat quasi minima nisi aliquid animi blanditiis nesciunt esse ea, culpa cumque, atque ex nihil labore.
              </div>
            </div>
          </div>
        ))
      ) : (
        <p>No results found</p>
      )}
      <h2 id='POSTS'>Posts:</h2>
      <div className="post-secondmain-container">
        <div className="all-posts">
          {Array.isArray(posts) && posts.length > 0 ? (
            posts.map((element) => (
              <Userpost key={element.id} post={element} />
            ))
          ) : (
            <p>No posts available</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
