import React, { useContext, useEffect, useState } from 'react';
import './style/Navbar.css';
import { useLocation, useNavigate, Link } from 'react-router-dom';
import realimage from './style/PartnurUp.png';
import searchbtn from './style/search-interface-symbol.png';
import NavbarContext from "../components/context/navbar/navbarContext";

const Navbar = () => {
    let navigate = useNavigate();
    const location = useLocation();
    
    const {  searchTerm, setSearchTerm, modalResult, searchResults } = useContext(NavbarContext);
    const a = useContext(NavbarContext)
    const logout = () => {
        sessionStorage.clear();
        navigate('/');
    };

    // Update search term and trigger data fetching
    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
    };

    useEffect(() => {
        if (searchTerm) {
            fetchData();
        }
    }, [searchTerm]);

    return (
        <div className='nav-main-container' style={location.pathname === '/' || location.pathname === '/paartnup' || location.pathname === '/post' || location.pathname === '/user-profile' ? {} : { display: 'none' }} >
            <div className="nav-first-container">
                <ul>
                    <img src={realimage} id='nav-logo' width={'50vw'} alt="an 3d art view" />
                    <li id='Logo'>Project Pals</li>
                    <li>
                        <div className="nav-search">
                            <input type="text" placeholder='Search...' value={searchTerm} onChange={handleSearch}/>
                            <img src={searchbtn} alt="search icon" onClick={a.fetchData()}/>
                        </div>
                    </li>
                    <li className='nav-list'>
                        <Link to={'/aboutus'}>About us</Link>
                    </li>
                    <li className='nav-list'>
                        <Link to={'/post'}>Posts</Link>
                    </li>
                    <li className='nav-list'>
                        <Link to={'/message'}>Messages</Link>
                    </li>
                    <li id='options'>Options</li>
                    {!(sessionStorage.getItem('token')) ? (
                        <div className='signup-button'>
                            <ul>
                                <li className="nav-signup">
                                    <Link to={'/signup'}><button className='nav-signup'>Sign Up</button></Link>
                                </li>
                                <li className="nav-signup">
                                    <Link to={'/login'}><button className='nav-signup'>Login</button></Link>
                                </li>
                            </ul>
                        </div>
                    ) : (
                        <ul>
                            <li className='nav-signup'><button className='nav-signup' onClick={logout}>Logout</button></li>
                        </ul>
                    )}
                </ul>
            </div>

            {/* Display search results */}
            {modalResult && searchResults.length > 0 && (
                <div className="search-results">
                    {searchResults.map((result) => (
                        <div key={result.id}>{result.title}</div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Navbar;
