import React, { useContext } from 'react';
import './style/Postitem.css';
import { useNavigate } from 'react-router-dom';
import NavbarContext from '../components/context/navbar/navbarContext'; // Import context

const Postitem = ({ post, showalert }) => {
  const token = sessionStorage.getItem('token');
  const navigate = useNavigate();
  
  // Access context values
  const { modalResult, searchResults } = useContext(NavbarContext);

  const clicked = async () => {
    const emails = post.email;

    let response = await fetch('http://127.0.0.1:8000/api/core/connect/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify({
        email: emails,
      }),
    });

    let json = await response.json();
    console.log(json);

    showalert('Email has been sent', 'success');
    // navigate("/message")
  };

  return (
    <div className='postitem-main-container'>
      <div className="postitem-first-container">
        <div className="postitem-subfirst-container">
          <ul style={modalResult ? { display: 'none' } : { display: 'flex' }}>
            <li id='name'> <div className="circle"></div>{post?.username}</li>
            <li className='connect-button'><button onClick={clicked}>Connect</button></li>
          </ul>
          <ul style={!modalResult ? { display: 'none' } : { display: 'flex' }}>
            <li id='name'> <div className="circle"></div>{searchResults.find(item => item.id === post.id)?.username}</li>
            <li className='connect-button'><button onClick={clicked}>Connect</button></li>
          </ul>
        </div>

        <div className="postitem-subsecond-container">
          <ul style={modalResult ? { display: 'none' } : { display: 'flex' }}>
            <li id='Title'>{post?.title}</li>
            <li id='Description'>{post?.description}</li>
          </ul>
        </div>
      
        <ul style={!modalResult ? { display: 'none' } : { display: 'flex' }}>
          <li id='Title'>{searchResults.find(item => item.id === post.id)?.title}</li>
          <li id='Description'>{searchResults.find(item => item.id === post.id)?.description}</li>
        </ul>
      </div>
    </div>
  );
};

export default Postitem;
