import { useState } from "react";
import NavbarContext from "./navbarContext";

const NavbarState = (props) => {
    const [modalResult, setModalResult] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const token = sessionStorage.getItem('token');

    const fetchData = async () => {
        if (!searchTerm) return;

        try {
            const response = await fetch(`http://127.0.0.1:8000/api/core/search/${searchTerm}`, {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });
            const data = await response.json();
            const actualData = data.payload;

            if (actualData.length > 0) {
                setSearchResults(actualData);
                setModalResult(true);
            } else {
                setSearchResults([]);
                setModalResult(false);
            }
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };
    const state =() =>{
        fetchData();
    }
    return (
        <NavbarContext.Provider value={{ state, searchTerm, setSearchTerm, modalResult, searchResults }}>
            {props.children}
        </NavbarContext.Provider>
    );
};

export default NavbarState;
